import React, { useState, useEffect } from 'react';
import './Products.css';  // Importa el archivo de CSS

const questionBank = {
  matematicas: {
    "4to": [
      {
        id: "mat4_1",
        topic: "Álgebra",
        question: "Si f(x) = 2x² + 3x - 5, ¿cuál es f(-2)?",
        options: ["3", "-1", "1", "5"],
        correctAnswer: 0,
        explanation: "Sustituimos x por -2: f(-2) = 2(-2)² + 3(-2) - 5 = 2(4) - 6 - 5 = 8 - 6 - 5 = -3"
      }
    ]
  },
  quimica: {
    "4to": [
      {
        id: "qui4_1",
        topic: "Átomos y moléculas",
        question: "¿Cuál es la fórmula química del agua?",
        options: ["H2O", "O2", "CO2", "CH4"],
        correctAnswer: 0,
        explanation: "La fórmula química del agua es H2O."
      }
    ]
  },
  fisica: {
    "4to": [
      {
        id: "fis4_1",
        topic: "Fuerzas y movimiento",
        question: "¿Qué ley describe el movimiento de los objetos?",
        options: ["Ley de la gravedad", "Ley de Newton", "Ley de Coulomb", "Ley de la conservación de la energía"],
        correctAnswer: 1,
        explanation: "La ley que describe el movimiento de los objetos es la Ley de Newton."
      }
    ]
  },
  biologia: {
    "4to": [
      {
        id: "bio4_1",
        topic: "Células",
        question: "¿Qué parte de la célula controla todas sus actividades?",
        options: ["Núcleo", "Membrana celular", "Citoplasma", "Mitocondria"],
        correctAnswer: 0,
        explanation: "El núcleo controla todas las actividades celulares."
      }
    ]
  }
};

export default function Products() {
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [answers, setAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(3);  // Corazones (vidas)
  const [timer, setTimer] = useState(0); // Cronómetro, inicia en 0
  const [xp, setXp] = useState(0); // Puntos de experiencia
  const [quizStarted, setQuizStarted] = useState(false); // Estado para saber si el cuestionario ha comenzado

  // Inicia el cronómetro
  useEffect(() => {
    let interval;
    if (quizStarted && timer > 0 && !showResults) {
      interval = setInterval(() => {
        setTimer(prevTimer => prevTimer - 1);
      }, 1000);
    } else if (timer === 0) {
      handleTimeUp();  // Cuando el tiempo se acaba
    }
    return () => clearInterval(interval);
  }, [timer, quizStarted, showResults, handleTimeUp]); // Agregar handleTimeUp en las dependencias

  const handleCourseSelect = (course) => {
    setSelectedCourse(course);
    setAnswers({});
    setScore(0);
    setLives(3); // Resetea vidas
    setTimer(60); // Resetea cronómetro a 60 segundos
    setXp(0); // Resetea XP
    setShowResults(false);
    setQuizStarted(false); // Resetea el estado de quizStarted
  };

  const handleAnswerSelect = (questionId, answerIndex) => {
    setAnswers({
      ...answers,
      [questionId]: answerIndex
    });
    if (answerIndex === questionBank[selectedCourse]["4to"].find(q => q.id === questionId).correctAnswer) {
      setScore(score + 1);
      setXp(xp + 10);  // Aumenta XP por respuesta correcta
    } else {
      setLives(lives - 1);  // Pierde un corazón por respuesta incorrecta
    }
  };

  const handleTimeUp = () => {
    setLives(lives - 1);  // Pierde un corazón cuando se acaba el tiempo
    setTimer(60); // Reinicia el cronómetro
  };

  const calculateScore = () => {
    if (!selectedCourse) return 0;
    const questions = questionBank[selectedCourse]["4to"];
    return questions.reduce((score, question) => {
      return score + (answers[question.id] === question.correctAnswer ? 1 : 0);
    }, 0);
  };

  const resetTrivia = () => {
    setSelectedCourse(null);
    setAnswers({});
    setLives(3);
    setTimer(60);
    setShowResults(false);
    setQuizStarted(false); // Resetea el estado del cuestionario
  };

  // Comienza el cuestionario y activa el temporizador
  const startQuiz = () => {
    setQuizStarted(true); // Activa el estado de que el cuestionario ha comenzado
  };

  return (
    <div className="container">
      <h1>Trivia Educativa</h1>

      {/* Mostrar el temporizador solo si el cuestionario ha comenzado */}
      {quizStarted && (
        <div className="timer">
          <span>Tiempo: {timer}s</span>
        </div>
      )}

      {/* Mostrar los corazones solo si el cuestionario ha comenzado */}
      {quizStarted && (
        <div className="hearts">
          <span>Vidas: </span>
          {[...Array(lives)].map((_, index) => (
            <span key={index} className="heart">❤️</span>
          ))}
        </div>
      )}

      {/* Selección de materia */}
      {!selectedCourse && (
        <div className="course-selection">
          <h2>Elige una materia:</h2>
          <div className="course-buttons">
            {Object.keys(questionBank).map(course => (
              <button
                key={course}
                onClick={() => handleCourseSelect(course)}
                className={`button ${selectedCourse === course ? 'selected-button' : ''}`}
              >
                {course.charAt(0).toUpperCase() + course.slice(1)}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Preguntas */}
      {selectedCourse && (
        <div>
          {questionBank[selectedCourse]["4to"].map((question) => (
            <div key={question.id} className="question-container">
              <h3 className="question-topic">{question.topic}</h3>
              <p>{question.question}</p>

              {question.options.map((option, oIndex) => (
                <div key={oIndex} className="answer-option">
                  <label>
                    <input
                      type="radio"
                      name={question.id}
                      checked={answers[question.id] === oIndex}
                      onChange={() => handleAnswerSelect(question.id, oIndex)}
                    />
                    {option}
                  </label>
                </div>
              ))}
            </div>
          ))}

          {!quizStarted ? (
            <div>
              <button
                onClick={startQuiz} // Inicia el cuestionario
                className="button"
              >
                Comenzar el Cuestionario
              </button>
            </div>
          ) : (
            <button
              onClick={() => setShowResults(true)}
              className="button"
            >
              Verificar Respuestas
            </button>
          )}

          {showResults && (
            <div className="result-container">
              <h3>Resultado Final:</h3>
              <p>
                Acertaste {calculateScore()} de {questionBank[selectedCourse]["4to"].length} preguntas
              </p>
              <div className="result-buttons">
                <button
                  onClick={() => {
                    setAnswers({});
                    setShowResults(false);
                    setQuizStarted(false); // Desactiva el estado de quizStarted
                  }}
                  className="reset-button"
                >
                  Intentar de nuevo
                </button>
                <button
                  onClick={resetTrivia}
                  className="change-course-button"
                >
                  Cambiar de materia
                </button>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
