import React, { useState, useEffect } from 'react';
import './Trivia.css';

const topics = {
  movimiento: {
    title: "Movimiento",
    subtopics: [
      "Conceptos básicos de cinemática",
      "Dinámica"
    ]
  },
  fuerzas: {
    title: "Fuerzas",
    subtopics: [
      "Tipos de fuerzas",
      "Análisis de sistemas en equilibrio y movimiento"
    ]
  },
  trabajoEnergia: {
    title: "Trabajo y energía",
    subtopics: [
      "Energía cinética",
      "Potencial",
      "Trabajo",
      "Conservación de la energía"
    ]
  },
  mecanica: {
    title: "Mecánica",
    subtopics: [
      "Estática",
      "Dinámica de rotación"
    ]
  }
};

const questionBank = {
  movimiento: [
    {
      id: "mov1_1",
      topic: "Movimiento",
      question: "Si un objeto se mueve a una velocidad constante de 10 m/s, ¿cuánto tiempo tardará en recorrer 50 metros?",
      options: ["5 s", "10 s", "15 s", "20 s"],
      correctAnswer: 0,
      explanation: "El tiempo se calcula con t = d/v = 50/10 = 5 s."
    },
    // ... resto de preguntas de movimiento
  ],
  fuerzas: [
    {
      id: "for2_1",
      topic: "Fuerzas",
      question: "Si una fuerza de 10 N actúa sobre un objeto de 2 kg, ¿cuál será su aceleración?",
      options: ["2 m/s²", "5 m/s²", "10 m/s²", "20 m/s²"],
      correctAnswer: 1,
      explanation: "a = F/m = 10/2 = 5 m/s²."
    },
    // ... resto de preguntas de fuerzas
  ],
  // ... resto de categorías
};

export default function PhysicsTrivia() {
  const [gameState, setGameState] = useState('topics'); // topics, questions, results
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [lives, setLives] = useState(3);
  const [timer, setTimer] = useState(60);
  const [xp, setXp] = useState(0);
  const [score, setScore] = useState(0);

  useEffect(() => {
    let interval;
    if (gameState === 'questions' && timer > 0) {
      interval = setInterval(() => {
        setTimer(prev => prev - 1);
      }, 1000);
    } else if (timer === 0) {
      handleTimeUp();
    }
    return () => clearInterval(interval);
  }, [timer, gameState]);

  const handleTopicSelect = (topic) => {
    setSelectedTopic(topic);
    setGameState('questions');
    resetGameState();
  };

  const handleAnswerSelect = (answerIndex) => {
    const currentQuestion = questionBank[selectedTopic][currentQuestionIndex];
    const isCorrect = answerIndex === currentQuestion.correctAnswer;

    setAnswers(prev => ({
      ...prev,
      [currentQuestion.id]: answerIndex
    }));

    if (isCorrect) {
      setScore(prev => prev + 100);
      setXp(prev => prev + 10);
    } else {
      setLives(prev => prev - 1);
    }

    if (currentQuestionIndex < questionBank[selectedTopic].length - 1) {
      setCurrentQuestionIndex(prev => prev + 1);
      setTimer(60);
    } else {
      setGameState('results');
    }
  };

  const handleTimeUp = () => {
    setLives(prev => prev - 1);
    if (lives > 1) {
      setTimer(60);
      setCurrentQuestionIndex(prev => prev + 1);
    } else {
      setGameState('results');
    }
  };

  const resetGameState = () => {
    setCurrentQuestionIndex(0);
    setAnswers({});
    setLives(3);
    setTimer(60);
    setScore(0);
    setXp(0);
  };

  const renderTopics = () => (
    <div className="p-4 space-y-4">
      <h2 className="text-2xl font-bold text-center mb-8">Selecciona un tema de Física</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {Object.entries(topics).map(([key, topic]) => (
          <div key={key} className="border rounded-lg p-4 hover:bg-blue-50 cursor-pointer"
               onClick={() => handleTopicSelect(key)}>
            <h3 className="text-xl font-semibold mb-2">{topic.title}</h3>
            <ul className="list-disc pl-5">
              {topic.subtopics.map((subtopic, index) => (
                <li key={index} className="text-gray-600">{subtopic}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );

  const renderQuestion = () => {
    const currentQuestion = questionBank[selectedTopic][currentQuestionIndex];
    return (
      <div className="p-4 max-w-2xl mx-auto">
        <div className="flex justify-between mb-4">
          <div className="space-x-1">
            {[...Array(lives)].map((_, i) => (
              <span key={i} className="text-2xl">❤️</span>
            ))}
          </div>
          <div className="text-xl font-bold">⏱️ {timer}s</div>
          <div className="text-xl">XP: {xp}</div>
        </div>

        <div className="bg-white rounded-lg p-6 shadow-lg">
          <h3 className="text-lg font-semibold mb-2">{currentQuestion.topic}</h3>
          <p className="text-xl mb-6">{currentQuestion.question}</p>

          <div className="space-y-3">
            {currentQuestion.options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleAnswerSelect(index)}
                className="w-full p-3 text-left rounded-lg border hover:bg-blue-50 transition-colors"
              >
                {option}
              </button>
            ))}
          </div>
        </div>
      </div>
    );
  };

  const renderResults = () => (
    <div className="p-4 text-center">
      <h2 className="text-3xl font-bold mb-4">¡Juego Terminado!</h2>
      <p className="text-xl mb-2">Puntuación: {score}</p>
      <p className="text-xl mb-4">XP ganado: {xp}</p>
      <button
        onClick={() => {
          setGameState('topics');
          setSelectedTopic(null);
          resetGameState();
        }}
        className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
      >
        Jugar de nuevo
      </button>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-blue-600 text-white p-4 text-center">
        <h1 className="text-3xl font-bold">Trivia de Física</h1>
      </header>

      {gameState === 'topics' && renderTopics()}
      {gameState === 'questions' && renderQuestion()}
      {gameState === 'results' && renderResults()}
    </div>
  );
}
