import React, { useState } from 'react';

const questionBank = {
  matematicas: {
    "4to": [
      {
        id: "mat4_1",
        topic: "Álgebra",
        question: "Si f(x) = 2x² + 3x - 5, ¿cuál es f(-2)?",
        options: ["3", "-1", "1", "5"],
        correctAnswer: 0,
        explanation: "Sustituimos x por -2: f(-2) = 2(-2)² + 3(-2) - 5 = 2(4) - 6 - 5 = 8 - 6 - 5 = -3"
      },
      // ... rest of the questions remain the same
    ],
    "5to": [/* ... existing 5to questions ... */]
  },
  quimica: {/* ... existing quimica questions ... */},
  biologia: {/* ... existing biologia questions ... */},
  fisica: {/* ... existing fisica questions ... */}
};

export default function Products() {
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [selectedYear, setSelectedYear] = useState(null);
  const [userAnswers, setUserAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [score, setScore] = useState(0);

  const handleCourseSelect = (course) => {
    setSelectedCourse(course);
    setSelectedYear(null);
    setUserAnswers({});
    setShowResults(false);
    setScore(0);
  };

  const handleYearSelect = (year) => {
    setSelectedYear(year);
    setUserAnswers({});
    setShowResults(false);
    setScore(0);
  };

  const handleAnswerSelect = (questionId, selectedOptionIndex) => {
    setUserAnswers(prev => ({
      ...prev,
      [questionId]: selectedOptionIndex
    }));
  };

  const calculateScore = () => {
    if (!selectedCourse || !selectedYear) return 0;

    const questions = questionBank[selectedCourse][selectedYear];
    let correct = 0;

    questions.forEach(question => {
      if (userAnswers[question.id] === question.correctAnswer) {
        correct += 1;
      }
    });

    return correct;
  };

  const handleSubmit = () => {
    const finalScore = calculateScore();
    setScore(finalScore);
    setShowResults(true);
  };

  const renderTrivia = () => {
    if (!selectedCourse || !selectedYear) return null;

    const questions = questionBank[selectedCourse][selectedYear];

    return (
      <div className="p-4">
        <h2 className="text-xl font-bold mb-4">Trivia de {selectedCourse} - {selectedYear} año</h2>
        {questions.map((question) => (
          <div key={question.id} className="mb-8 p-4 border rounded-lg shadow-sm">
            <h3 className="font-bold mb-2">{question.topic}</h3>
            <p className="mb-4">{question.question}</p>
            <div className="space-y-2">
              {question.options.map((option, index) => (
                <div key={index} className="flex items-center">
                  <input
                    type="radio"
                    id={`${question.id}-${index}`}
                    name={question.id}
                    value={index}
                    checked={userAnswers[question.id] === index}
                    onChange={() => handleAnswerSelect(question.id, index)}
                    className="mr-2"
                  />
                  <label htmlFor={`${question.id}-${index}`}>{option}</label>
                </div>
              ))}
            </div>
            {showResults && (
              <div className="mt-4">
                <p className={`font-bold ${userAnswers[question.id] === question.correctAnswer ? 'text-green-600' : 'text-red-600'}`}>
                  {userAnswers[question.id] === question.correctAnswer ? '¡Correcto!' : 'Incorrecto'}
                </p>
                <p className="mt-2 italic">{question.explanation}</p>
              </div>
            )}
          </div>
        ))}

        {!showResults && Object.keys(userAnswers).length === questions.length && (
          <button
            onClick={handleSubmit}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors"
          >
            Verificar respuestas
          </button>
        )}

        {showResults && (
          <div className="mt-4 p-4 bg-gray-100 rounded-lg">
            <h3 className="font-bold text-lg">Resultados:</h3>
            <p>Obtuviste {score} de {questions.length} respuestas correctas</p>
            <button
              onClick={() => {
                setUserAnswers({});
                setShowResults(false);
                setScore(0);
              }}
              className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors"
            >
              Intentar de nuevo
            </button>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6">Selecciona un curso</h1>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
        {['matematicas', 'quimica', 'biologia', 'fisica'].map((course) => (
          <button
            key={course}
            onClick={() => handleCourseSelect(course)}
            className={`p-2 rounded ${
              selectedCourse === course
                ? 'bg-blue-500 text-white'
                : 'bg-gray-200 hover:bg-gray-300'
            }`}
          >
            {course.charAt(0).toUpperCase() + course.slice(1)}
          </button>
        ))}
      </div>

      {selectedCourse && (
        <div className="mb-6">
          <h2 className="text-xl font-bold mb-4">Selecciona el año</h2>
          <div className="grid grid-cols-2 gap-4">
            {['4to', '5to'].map((year) => (
              <button
                key={year}
                onClick={() => handleYearSelect(year)}
                className={`p-2 rounded ${
                  selectedYear === year
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-200 hover:bg-gray-300'
                }`}
              >
                {year === '4to' ? '1.º año de BGU (4.º curso)' : '2.º año de BGU (5.º curso)'}
              </button>
            ))}
          </div>
        </div>
      )}

      {renderTrivia()}
    </div>
  );
}
