import React, { useState, useEffect } from 'react';

const questionBank = {
  matematicas: {
    "4to": [
      {
        id: "mat4_1",
        topic: "Álgebra",
        question: "Si f(x) = 2x² + 3x - 5, ¿cuál es f(-2)?",
        options: ["3", "-1", "1", "5"],
        correctAnswer: 0,
        explanation: "Sustituimos x por -2: f(-2) = 2(-2)² + 3(-2) - 5 = 2(4) - 6 - 5 = 8 - 6 - 5 = -3"
      }
    ]
  }
};

export default function Trivia() {
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [answers, setAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [score, setScore] = useState(0);
  const [lives, setLives] = useState(3);  // Corazones (vidas)
  const [timer, setTimer] = useState(30); // Cronómetro
  const [xp, setXp] = useState(0); // Puntos de experiencia

  // Inicia el cronómetro
  useEffect(() => {
    let interval;
    if (timer > 0 && !showResults) {
      interval = setInterval(() => {
        setTimer(prevTimer => prevTimer - 1);
      }, 1000);
    } else if (timer === 0) {
      handleTimeUp();  // Cuando el tiempo se acaba
    }
    return () => clearInterval(interval);
  }, [timer, showResults]);

  const handleCourseSelect = (course) => {
    setSelectedCourse(course);
    setAnswers({});
    setScore(0);
    setLives(3); // Resetea vidas
    setTimer(30); // Resetea cronómetro
    setXp(0); // Resetea XP
    setShowResults(false);
  };

  const handleAnswerSelect = (questionId, answerIndex) => {
    setAnswers({
      ...answers,
      [questionId]: answerIndex
    });
    if (answerIndex === questionBank[selectedCourse]["4to"].find(q => q.id === questionId).correctAnswer) {
      setScore(score + 1);
      setXp(xp + 10);  // Aumenta XP por respuesta correcta
    } else {
      setLives(lives - 1);  // Pierde un corazón por respuesta incorrecta
    }
  };

  const handleTimeUp = () => {
    setLives(lives - 1);  // Pierde un corazón cuando se acaba el tiempo
    setTimer(30); // Reinicia el cronómetro
  };

  const calculateScore = () => {
    if (!selectedCourse) return 0;
    const questions = questionBank[selectedCourse]["4to"];
    return questions.reduce((score, question) => {
      return score + (answers[question.id] === question.correctAnswer ? 1 : 0);
    }, 0);
  };

  const resetTrivia = () => {
    setSelectedCourse(null);
    setAnswers({});
    setLives(3);
    setTimer(30);
    setShowResults(false);
  };

  const buttonStyle = {
    padding: '10px 20px',
    backgroundColor: '#ffbc00',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    margin: '5px',
    minWidth: '150px',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
    fontSize: '16px',
    fontWeight: 'bold'
  };

  const selectedButtonStyle = {
    ...buttonStyle,
    backgroundColor: '#ff6600',
    transform: 'scale(1.1)',
    boxShadow: '0px 4px 10px rgba(0,0,0,0.2)'
  };

  return (
    <div style={{
      maxWidth: '800px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#282828',
      borderRadius: '10px',
      boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
      color: '#fff',
      fontFamily: 'Arial, sans-serif',
      backgroundImage: 'linear-gradient(to bottom right, #0f0c29, #302b63, #24243e)',
      backgroundSize: 'cover',
      textAlign: 'center'
    }}>
      <h1 style={{
        fontSize: '34px',
        marginBottom: '20px',
        color: '#ffbc00',
        textShadow: '2px 2px 5px rgba(0, 0, 0, 0.6)'
      }}>
        Trivia Educativa
      </h1>

      {/* Mostramos los corazones (vidas) */}
      <div style={{ marginBottom: '20px', fontSize: '20px' }}>
        <span>Vidas: </span>
        {[...Array(lives)].map((_, index) => (
          <span key={index} style={{ color: 'red', fontSize: '24px' }}>❤️</span>
        ))}
      </div>

      {/* Cronómetro */}
      <div style={{ marginBottom: '20px', fontSize: '30px' }}>
        <span>Tiempo: {timer}s</span>
      </div>

      {/* Selección de materia */}
      {!selectedCourse && (
        <div style={{ marginBottom: '20px' }}>
          <h2 style={{ marginBottom: '10px', fontSize: '22px' }}>Elige una materia:</h2>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
            gap: '10px',
            justifyContent: 'center'
          }}>
            {Object.keys(questionBank).map(course => (
              <button
                key={course}
                onClick={() => handleCourseSelect(course)}
                style={selectedCourse === course ? selectedButtonStyle : buttonStyle}
              >
                {course.charAt(0).toUpperCase() + course.slice(1)}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Preguntas */}
      {selectedCourse && (
        <div>
          {questionBank[selectedCourse]["4to"].map((question) => (
            <div
              key={question.id}
              style={{
                marginBottom: '20px',
                padding: '20px',
                border: '1px solid #ffbc00',
                borderRadius: '10px',
                backgroundColor: '#333',
                boxShadow: '0 4px 10px rgba(0,0,0,0.3)',
                color: '#fff',
                fontSize: '18px',
                fontWeight: 'normal'
              }}
            >
              <h3 style={{
                marginBottom: '10px',
                color: '#ffbc00',
                fontSize: '24px',
                textShadow: '1px 1px 5px rgba(0, 0, 0, 0.5)'
              }}>
                {question.topic}
              </h3>
              <p style={{ marginBottom: '15px', fontSize: '18px' }}>{question.question}</p>

              {question.options.map((option, oIndex) => (
                <div key={oIndex} style={{ marginBottom: '10px' }}>
                  <label style={{
                    display: 'flex',
                    alignItems: 'center',
                    cursor: 'pointer',
                    padding: '8px',
                    backgroundColor: answers[question.id] === oIndex ? '#ff6600' : 'transparent',
                    borderRadius: '4px',
                    transition: 'background-color 0.2s ease',
                    fontSize: '18px'
                  }}>
                    <input
                      type="radio"
                      name={question.id}
                      checked={answers[question.id] === oIndex}
                      onChange={() => handleAnswerSelect(question.id, oIndex)}
                      style={{ marginRight: '10px' }}
                    />
                    {option}
                  </label>
                </div>
              ))}
            </div>
          ))}

          {!showResults && (
            <div style={{ textAlign: 'center', marginTop: '20px' }}>
              <button
                onClick={() => setShowResults(true)}
                style={{
                  ...buttonStyle,
                  backgroundColor: '#28a745',
                  color: 'white',
                  fontSize: '20px'
                }}
              >
                Verificar Respuestas
              </button>
            </div>
          )}

          {showResults && (
            <div style={{
              marginTop: '20px',
              padding: '20px',
              backgroundColor: '#444',
              borderRadius: '5px',
              textAlign: 'center'
            }}>
              <h3 style={{ color: '#ffbc00', fontSize: '24px' }}>Resultado Final:</h3>
              <p style={{ fontSize: '22px', margin: '10px 0', color: '#fff' }}>
                Acertaste {calculateScore()} de {questionBank[selectedCourse]["4to"].length} preguntas
              </p>
              <div style={{ display: 'flex', gap: '10px', justifyContent: 'center', marginTop: '15px' }}>
                <button
                  onClick={() => {
                    setAnswers({});
                    setShowResults(false);
                  }}
                  style={{
                    ...buttonStyle,
                    backgroundColor: '#ff6600',
                    color: 'white',
                    fontSize: '18px'
                  }}
                >
                  Intentar de nuevo
                </button>
                <button
                  onClick={resetTrivia}
                  style={{
                    ...buttonStyle,
                    backgroundColor: '#6c757d',
                    color: 'white',
                    fontSize: '18px'
                  }}
                >
                  Cambiar de materia
                </button>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
