import React, { useState } from 'react';
import './Products.css';

const questionBank = {
  matematicas: {
    "4to": [
      { id: "mat4_1", topic: "Álgebra", question: "Si f(x) = 2x² + 3x - 5, ¿cuál es f(-2)?", options: ["3", "-1", "1", "5"], correctAnswer: 0, explanation: "Sustituimos x por -2: f(-2) = 2(-2)² + 3(-2) - 5 = 2(4) - 6 - 5 = -3" },
      { id: "mat4_2", topic: "Geometría Analítica", question: "¿Cuál es la pendiente de una recta que pasa por los puntos (2,3) y (4,7)?", options: ["2", "3", "4", "5"], correctAnswer: 0, explanation: "La pendiente se calcula con (y₂-y₁)/(x₂-x₁) = (7-3)/(4-2) = 4/2 = 2" }
    ]
  },
  quimica: {
    "4to": [
      { id: "qui4_1", topic: "Estructura Atómica", question: "¿Cuántos electrones tiene un átomo neutro de sodio (Na)?", options: ["11", "10", "12", "13"], correctAnswer: 0, explanation: "El número atómico del sodio es 11, por lo tanto tiene 11 protones y 11 electrones" }
    ]
  }
};

function Products() {
  const [selectedTopic, setSelectedTopic] = useState("");
  const [selectedYear, setSelectedYear] = useState("");
  const [questions, setQuestions] = useState([]);

  const handleTopicChange = (event) => {
    const topic = event.target.value;
    setSelectedTopic(topic);
    setQuestions([]);
  };

  const handleYearChange = (event) => {
    const year = event.target.value;
    setSelectedYear(year);
    if (selectedTopic) {
      setQuestions(questionBank[selectedTopic][year] || []);
    }
  };

  return (
    <div className="products">
      <h1>Selecciona un Curso y Tema</h1>
      <div>
        <label>Selecciona un tema:</label>
        <select value={selectedTopic} onChange={handleTopicChange}>
          <option value="">--Selecciona un tema--</option>
          <option value="matematicas">Matemáticas</option>
          <option value="quimica">Química</option>
        </select>
      </div>

      {selectedTopic && (
        <div>
          <label>Selecciona un año:</label>
          <select value={selectedYear} onChange={handleYearChange}>
            <option value="">--Selecciona un año--</option>
            <option value="4to">1.º año de BGU (4.º curso)</option>
            <option value="5to">2.º año de BGU (5.º curso)</option>
          </select>
        </div>
      )}

      {questions.length > 0 && (
        <div className="questions">
          <h2>Trivia de {selectedTopic} - {selectedYear}</h2>
          {questions.map((question) => (
            <div key={question.id} className="question">
              <p>{question.question}</p>
              {question.options.map((option, index) => (
                <button key={index}>{option}</button>
              ))}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Products;
