import React, { useState } from 'react';

const questionBank = {
  matematicas: {
    "4to": [
      {
        id: "mat4_1",
        topic: "Álgebra",
        question: "Si f(x) = 2x² + 3x - 5, ¿cuál es f(-2)?",
        options: ["3", "-1", "1", "5"],
        correctAnswer: 0,
        explanation: "Sustituimos x por -2: f(-2) = 2(-2)² + 3(-2) - 5 = 2(4) - 6 - 5 = 8 - 6 - 5 = -3"
      },
      {
        id: "mat4_2",
        topic: "Geometría Analítica",
        question: "¿Cuál es la pendiente de una recta que pasa por los puntos (2,3) y (4,7)?",
        options: ["2", "3", "4", "5"],
        correctAnswer: 0,
        explanation: "La pendiente se calcula con (y₂-y₁)/(x₂-x₁) = (7-3)/(4-2) = 4/2 = 2"
      }
    ],
    "5to": [
      {
        id: "mat5_1",
        topic: "Cálculo Diferencial",
        question: "¿Cuál es la derivada de f(x) = x³ + 2x?",
        options: ["3x² + 2", "3x² + 1", "2x³ + 2", "x² + 2x"],
        correctAnswer: 0,
        explanation: "Aplicando las reglas de derivación: d/dx(x³) = 3x² y d/dx(2x) = 2"
      }
    ]
  },
  fisica: {
    "4to": [
      {
        id: "fis4_1",
        topic: "Cinemática",
        question: "Un objeto se mueve con velocidad constante de 5 m/s durante 10 segundos. ¿Qué distancia recorre?",
        options: ["50 metros", "25 metros", "100 metros", "10 metros"],
        correctAnswer: 0,
        explanation: "Distancia = velocidad × tiempo = 5 m/s × 10 s = 50 m"
      }
    ],
    "5to": [
      {
        id: "fis5_1",
        topic: "Electricidad",
        question: "¿Cuál es la resistencia de un conductor si al aplicar 12V circula una corriente de 2A?",
        options: ["6 Ω", "24 Ω", "10 Ω", "4 Ω"],
        correctAnswer: 0,
        explanation: "Por la ley de Ohm: R = V/I = 12V/2A = 6Ω"
      }
    ]
  }
};

export default function Trivia() {
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [selectedYear, setSelectedYear] = useState(null);
  const [answers, setAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);

  const handleCourseSelect = (course) => {
    setSelectedCourse(course);
    setSelectedYear(null);
    setAnswers({});
    setShowResults(false);
  };

  const handleYearSelect = (year) => {
    setSelectedYear(year);
    setAnswers({});
    setShowResults(false);
  };

  const handleAnswerSelect = (questionId, answerIndex) => {
    setAnswers({
      ...answers,
      [questionId]: answerIndex
    });
  };

  const calculateScore = () => {
    if (!selectedCourse || !selectedYear) return 0;
    const questions = questionBank[selectedCourse][selectedYear];
    return questions.reduce((score, question) => {
      return score + (answers[question.id] === question.correctAnswer ? 1 : 0);
    }, 0);
  };

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <h1 style={{ fontSize: '24px', marginBottom: '20px' }}>Trivia Educativa</h1>

      {/* Selección de curso */}
      <div style={{ marginBottom: '20px' }}>
        <h2 style={{ marginBottom: '10px' }}>Selecciona un curso:</h2>
        <div style={{ display: 'flex', gap: '10px' }}>
          {Object.keys(questionBank).map(course => (
            <button
              key={course}
              onClick={() => handleCourseSelect(course)}
              style={{
                padding: '10px 20px',
                backgroundColor: selectedCourse === course ? '#007bff' : '#e9ecef',
                color: selectedCourse === course ? 'white' : 'black',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              {course.charAt(0).toUpperCase() + course.slice(1)}
            </button>
          ))}
        </div>
      </div>

      {/* Selección de año */}
      {selectedCourse && (
        <div style={{ marginBottom: '20px' }}>
          <h2 style={{ marginBottom: '10px' }}>Selecciona el año:</h2>
          <div style={{ display: 'flex', gap: '10px' }}>
            {Object.keys(questionBank[selectedCourse]).map(year => (
              <button
                key={year}
                onClick={() => handleYearSelect(year)}
                style={{
                  padding: '10px 20px',
                  backgroundColor: selectedYear === year ? '#007bff' : '#e9ecef',
                  color: selectedYear === year ? 'white' : 'black',
                  border: 'none',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                {year === '4to' ? '1º BGU' : '2º BGU'}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Preguntas */}
      {selectedCourse && selectedYear && (
        <div>
          {questionBank[selectedCourse][selectedYear].map((question, qIndex) => (
            <div
              key={question.id}
              style={{
                marginBottom: '20px',
                padding: '20px',
                border: '1px solid #dee2e6',
                borderRadius: '5px'
              }}
            >
              <h3 style={{ marginBottom: '10px' }}>{question.topic}</h3>
              <p style={{ marginBottom: '15px' }}>{question.question}</p>

              {question.options.map((option, oIndex) => (
                <div key={oIndex} style={{ marginBottom: '10px' }}>
                  <label style={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }}>
                    <input
                      type="radio"
                      name={question.id}
                      checked={answers[question.id] === oIndex}
                      onChange={() => handleAnswerSelect(question.id, oIndex)}
                      style={{ marginRight: '10px' }}
                    />
                    {option}
                  </label>
                </div>
              ))}

              {showResults && (
                <div style={{ marginTop: '10px', padding: '10px', backgroundColor: '#f8f9fa' }}>
                  <p style={{ 
                    color: answers[question.id] === question.correctAnswer ? 'green' : 'red',
                    fontWeight: 'bold'
                  }}>
                    {answers[question.id] === question.correctAnswer ? '¡Correcto!' : 'Incorrecto'}
                  </p>
                  <p style={{ marginTop: '5px', fontStyle: 'italic' }}>{question.explanation}</p>
                </div>
              )}
            </div>
          ))}

          <button
            onClick={() => setShowResults(true)}
            style={{
              padding: '10px 20px',
              backgroundColor: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              marginTop: '20px'
            }}
          >
            Verificar Respuestas
          </button>

          {showResults && (
            <div style={{ marginTop: '20px', padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '5px' }}>
              <h3>Resultado Final:</h3>
              <p>Acertaste {calculateScore()} de {questionBank[selectedCourse][selectedYear].length} preguntas</p>
              <button
                onClick={() => {
                  setAnswers({});
                  setShowResults(false);
                }}
                style={{
                  padding: '10px 20px',
                  backgroundColor: '#007bff',
                  color: 'white',
                  border: 'none',
                  borderRadius: '5px',
                  cursor: 'pointer',
                  marginTop: '10px'
                }}
              >
                Intentar de nuevo
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
