import React, { useState } from 'react';
import '../../App.css';

const questionBank = {
  matematicas: {
    "4to": [
      {
        id: "mat4_1",
        topic: "Álgebra",
        question: "Si f(x) = 2x² + 3x - 5, ¿cuál es f(-2)?",
        options: [
          "3",
          "-1",
          "1",
          "5"
        ],
        correctAnswer: 0,
        explanation: "Sustituimos x por -2: f(-2) = 2(-2)² + 3(-2) - 5 = 2(4) - 6 - 5 = 8 - 6 - 5 = -3"
      },
      {
        id: "mat4_2",
        topic: "Geometría Analítica",
        question: "¿Cuál es la pendiente de una recta que pasa por los puntos (2,3) y (4,7)?",
        options: [
          "2",
          "3",
          "4",
          "5"
        ],
        correctAnswer: 0,
        explanation: "La pendiente se calcula con (y₂-y₁)/(x₂-x₁) = (7-3)/(4-2) = 4/2 = 2"
      },
      {
        id: "mat4_3",
        topic: "Funciones",
        question: "¿Cuál es el dominio de la función f(x) = √(x + 2)?",
        options: [
          "x ≥ -2",
          "x > -2",
          "x ≤ -2",
          "x < -2"
        ],
        correctAnswer: 0,
        explanation: "Para que una raíz cuadrada esté definida, el radicando debe ser mayor o igual a cero: x + 2 ≥ 0, por lo tanto x ≥ -2"
      }
    ],
    "5to": [
      {
        id: "mat5_1",
        topic: "Cálculo Diferencial",
        question: "¿Cuál es la derivada de f(x) = x³ + 2x?",
        options: [
          "3x² + 2",
          "3x² + 1",
          "2x³ + 2",
          "x² + 2x"
        ],
        correctAnswer: 0,
        explanation: "Aplicando las reglas de derivación: d/dx(x³) = 3x² y d/dx(2x) = 2"
      },
      {
        id: "mat5_2",
        topic: "Probabilidad",
        question: "Al lanzar dos dados, ¿cuál es la probabilidad de obtener una suma de 7?",
        options: [
          "1/6",
          "1/12",
          "1/3",
          "1/4"
        ],
        correctAnswer: 0,
        explanation: "Hay 6 combinaciones favorables (1,6), (2,5), (3,4), (4,3), (5,2), (6,1) de 36 posibles combinaciones"
      }
    ]
  },
  // Las demás materias se mantienen igual...
};

export default function Products() {
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [selectedYear, setSelectedYear] = useState(null);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [feedback, setFeedback] = useState("");
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const handleCourseSelect = (course) => {
    setSelectedCourse(course);
    setSelectedYear(null); // Reset year selection
    setSelectedAnswer(null); // Reset selected answer
    setFeedback(""); // Reset feedback
    setCurrentQuestionIndex(0); // Reset question index
  };

  const handleYearSelect = (year) => {
    setSelectedYear(year);
  };

  const handleAnswerSelect = (index) => {
    setSelectedAnswer(index);
    const correctIndex = questionBank[selectedCourse][selectedYear][currentQuestionIndex].correctAnswer;
    if (index === correctIndex) {
      setFeedback("¡Respuesta Correcta!");
    } else {
      setFeedback("Respuesta Incorrecta. Intenta de nuevo.");
    }
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < questionBank[selectedCourse][selectedYear].length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setSelectedAnswer(null);
      setFeedback("");
    } else {
      setFeedback("¡Has terminado la trivia!");
    }
  };

  const renderTrivia = () => {
    if (!selectedCourse || !selectedYear) return null;

    const questions = questionBank[selectedCourse][selectedYear];
    const currentQuestion = questions[currentQuestionIndex];

    return (
      <div className="trivia">
        <h2>Trivia de {selectedCourse} - {selectedYear} año</h2>
        <div className="question">
          <h3>{currentQuestion.topic}</h3>
          <p>{currentQuestion.question}</p>
          <ul>
            {currentQuestion.options.map((option, index) => (
              <li
                key={index}
                onClick={() => handleAnswerSelect(index)}
                className={selectedAnswer === index ? (index === currentQuestion.correctAnswer ? "correct" : "incorrect") : ""}
              >
                {option}
              </li>
            ))}
          </ul>
          <p>{feedback}</p>
          <button onClick={handleNextQuestion}>Siguiente pregunta</button>
        </div>
      </div>
    );
  };

  return (
    <div className='products'>
      <h1 className='products-title'>Selecciona un curso</h1>
      <div className='course-selection'>
        <button onClick={() => handleCourseSelect('matematicas')}>Matemáticas</button>
        <button onClick={() => handleCourseSelect('quimica')}>Química</button>
        <button onClick={() => handleCourseSelect('biologia')}>Biología</button>
        <button onClick={() => handleCourseSelect('fisica')}>Física</button>
      </div>
      {selectedCourse && (
        <div className='year-selection'>
          <h2>Selecciona el año</h2>
          <button onClick={() => handleYearSelect('4to')}>1.º año de BGU (4.º curso)</button>
          <button onClick={() => handleYearSelect('5to')}>2.º año de BGU (5.º curso)</button>
        </div>
      )}
      {renderTrivia()}
    </div>
  );
}
