import React from 'react';
import { Link } from 'react-router-dom';
import './Cards.css'; // Asegúrate de que el archivo CSS está vinculado

function CardItem(props) {
  const isExternal = props.external;

  return (
    <li className="card-item">
      {isExternal ? (
        <a
          className="card-item__link"
          href={props.path}
          target="_blank"
          rel="noopener noreferrer"
        >
          <figure className="card-item__pic-wrap" data-category={props.label}>
            <img
              className="card-item__img"
              alt="Service Image"
              src={props.src}
            />
          </figure>
          <div className="card-item__info">
            <h5 className="card-item__text">{props.text}</h5>
          </div>
        </a>
      ) : (
        <Link className="card-item__link" to={props.path}>
          <figure className="card-item__pic-wrap" data-category={props.label}>
            <img
              className="card-item__img"
              alt="Service Image"
              src={props.src}
            />
          </figure>
          <div className="card-item__info">
            <h5 className="card-item__text">{props.text}</h5>
          </div>
        </Link>
      )}
    </li>
  );
}

export default CardItem;
