import React, { useState, useEffect } from 'react';

const questionBank = {
  matematicas: {
    "4to": [
      {
        id: "mat4_1",
        topic: "√Ålgebra",
        question: "Si f(x) = 2x¬≤ + 3x - 5, ¬øcu√°l es f(-2)?",
        options: ["3", "-1", "1", "5"],
        correctAnswer: 0,
        points: 100,
        explanation: "Sustituimos x por -2: f(-2) = 2(-2)¬≤ + 3(-2) - 5 = 2(4) - 6 - 5 = -3"
      }
    ],
    "5to": [
      {
        id: "mat5_1",
        topic: "C√°lculo Diferencial",
        question: "¬øCu√°l es la derivada de f(x) = x¬≥ + 2x?",
        options: ["3x¬≤ + 2", "3x¬≤ + 1", "2x¬≥ + 2", "x¬≤ + 2x"],
        correctAnswer: 0,
        points: 100,
        explanation: "Aplicando las reglas de derivaci√≥n: d/dx(x¬≥) = 3x¬≤ y d/dx(2x) = 2"
      }
    ]
  },
  fisica: {
    "4to": [
      {
        id: "fis4_1",
        topic: "Cinem√°tica",
        question: "Un objeto se mueve con velocidad constante de 5 m/s durante 10 segundos. ¬øQu√© distancia recorre?",
        options: ["50 metros", "25 metros", "100 metros", "10 metros"],
        correctAnswer: 0,
        points: 100,
        explanation: "Distancia = velocidad √ó tiempo = 5 m/s √ó 10 s = 50 m"
      }
    ],
    "5to": [
      {
        id: "fis5_1",
        topic: "Electricidad",
        question: "¬øCu√°l es la resistencia de un conductor si al aplicar 12V circula una corriente de 2A?",
        options: ["6 Œ©", "24 Œ©", "10 Œ©", "4 Œ©"],
        correctAnswer: 0,
        points: 100,
        explanation: "Por la ley de Ohm: R = V/I = 12V/2A = 6Œ©"
      }
    ]
  }
};

export default function Trivia() {
  const [selectedYear, setSelectedYear] = useState(null);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [answers, setAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [yearLocked, setYearLocked] = useState(false);
  const [score, setScore] = useState(0);
  const [streak, setStreak] = useState(0);
  const [timeLeft, setTimeLeft] = useState(30);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    let timer;
    if (isPlaying && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);
    } else if (timeLeft === 0) {
      setShowResults(true);
    }
    return () => clearInterval(timer);
  }, [isPlaying, timeLeft]);

  const handleYearSelect = (year) => {
    setSelectedYear(year);
    setYearLocked(true);
    setSelectedCourse(null);
    setAnswers({});
    setShowResults(false);
    setScore(0);
    setStreak(0);
  };

  const handleCourseSelect = (course) => {
    setSelectedCourse(course);
    setAnswers({});
    setShowResults(false);
    setTimeLeft(30);
    setIsPlaying(true);
  };

  const handleAnswerSelect = (questionId, answerIndex) => {
    const currentQuestion = questionBank[selectedCourse][selectedYear].find(q => q.id === questionId);
    const isCorrect = currentQuestion.correctAnswer === answerIndex;

    setAnswers(prev => ({
      ...prev,
      [questionId]: answerIndex
    }));

    if (isCorrect) {
      setScore(prev => prev + currentQuestion.points);
      setStreak(prev => prev + 1);
    } else {
      setStreak(0);
    }
  };

  const resetTrivia = () => {
    setYearLocked(false);
    setSelectedYear(null);
    setSelectedCourse(null);
    setAnswers({});
    setShowResults(false);
    setScore(0);
    setStreak(0);
    setIsPlaying(false);
    setTimeLeft(30);
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      {/* Barra de estado del juego */}
      {isPlaying && (
        <div className="flex justify-between items-center bg-gray-100 p-4 rounded-lg mb-6">
          <div className="flex items-center space-x-4">
            <div className="flex items-center">
              <span className="text-yellow-500 font-bold">‚≠ê</span>
              <span className="ml-2">{score} puntos</span>
            </div>
            <div className="flex items-center">
              <span className="text-blue-500 font-bold">üèÜ</span>
              <span className="ml-2">Racha: {streak}</span>
            </div>
          </div>
          <div className="flex items-center">
            <span className="text-red-500 font-bold">‚è±Ô∏è</span>
            <span className="ml-2">{timeLeft}s</span>
          </div>
        </div>
      )}

      <h1 className="text-2xl font-bold text-center mb-6">Trivia Educativa</h1>

      {/* Selecci√≥n de a√±o */}
      {!yearLocked && (
        <div className="text-center mb-6">
          <h2 className="text-xl mb-4">Selecciona el a√±o:</h2>
          <div className="flex justify-center gap-4">
            {['4to', '5to'].map(year => (
              <button
                key={year}
                onClick={() => handleYearSelect(year)}
                className={`px-6 py-3 rounded-lg transition-colors ${
                  selectedYear === year 
                    ? 'bg-blue-500 text-white' 
                    : 'bg-gray-200 hover:bg-gray-300'
                }`}
              >
                {year === '4to' ? '1¬∫ BGU' : '2¬∫ BGU'}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Selecci√≥n de materia */}
      {selectedYear && !selectedCourse && (
        <div className="text-center mb-6">
          <h2 className="text-xl mb-4">Selecciona la materia:</h2>
          <div className="grid grid-cols-2 gap-4">
            {Object.keys(questionBank).map(course => (
              <button
                key={course}
                onClick={() => handleCourseSelect(course)}
                className="p-4 bg-white rounded-lg shadow hover:shadow-lg transition-shadow"
              >
                <div className="text-lg capitalize">{course}</div>
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Preguntas */}
      {selectedYear && selectedCourse && !showResults && (
        <div className="space-y-6">
          {questionBank[selectedCourse][selectedYear].map((question) => (
            <div key={question.id} className="bg-white rounded-lg shadow p-6">
              <h3 className="font-bold mb-2">{question.topic}</h3>
              <p className="mb-4">{question.question}</p>

              <div className="space-y-2">
                {question.options.map((option, idx) => (
                  <button
                    key={idx}
                    onClick={() => handleAnswerSelect(question.id, idx)}
                    className={`w-full p-3 text-left rounded-lg transition-colors ${
                      answers[question.id] === idx 
                        ? 'bg-blue-100' 
                        : 'hover:bg-gray-100'
                    }`}
                  >
                    {option}
                  </button>
                ))}
              </div>
            </div>
          ))}

          <div className="text-center">
            <button
              onClick={() => setShowResults(true)}
              className="px-6 py-3 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors"
            >
              Verificar Respuestas
            </button>
          </div>
        </div>
      )}

      {/* Resultados */}
      {showResults && (
        <div className="bg-white rounded-lg shadow p-6 text-center">
          <h2 className="text-2xl font-bold mb-4">¬°Juego terminado! üéÆ</h2>
          <p className="text-xl mb-4">Puntuaci√≥n final: {score} ‚≠ê</p>
          <p className="mb-4">Racha m√°s larga: {streak} üèÜ</p>

          {questionBank[selectedCourse][selectedYear].map((question) => (
            <div key={question.id} className="mb-4 p-4 bg-gray-50 rounded-lg">
              <p className="font-bold">{question.question}</p>
              <p className={`mt-2 ${
                answers[question.id] === question.correctAnswer 
                  ? 'text-green-600' 
                  : 'text-red-600'
              }`}>
                {answers[question.id] === question.correctAnswer 
                  ? '¬°Correcto! ‚úÖ' 
                  : 'Incorrecto ‚ùå'}
              </p>
              <p className="mt-2 text-gray-600">{question.explanation}</p>
            </div>
          ))}

          <div className="flex justify-center gap-4 mt-6">
            <button
