import React, { useState, useEffect } from 'react';

const questionBank = {
  matematicas: {
    "4to": [
      {
        id: "mat4_1",
        topic: "Álgebra",
        question: "Si f(x) = 2x² + 3x - 5, ¿cuál es f(-2)?",
        options: ["3", "-1", "1", "5"],
        correctAnswer: 0,
        points: 100,
        explanation: "Sustituimos x por -2: f(-2) = 2(-2)² + 3(-2) - 5 = 2(4) - 6 - 5 = -3"
      }
    ],
    "5to": [
      {
        id: "mat5_1",
        topic: "Cálculo Diferencial",
        question: "¿Cuál es la derivada de f(x) = x³ + 2x?",
        options: ["3x² + 2", "3x² + 1", "2x³ + 2", "x² + 2x"],
        correctAnswer: 0,
        points: 100,
        explanation: "Aplicando las reglas de derivación: d/dx(x³) = 3x² y d/dx(2x) = 2"
      }
    ]
  },
  fisica: {
    "4to": [
      {
        id: "fis4_1",
        topic: "Cinemática",
        question: "Un objeto se mueve con velocidad constante de 5 m/s durante 10 segundos. ¿Qué distancia recorre?",
        options: ["50 metros", "25 metros", "100 metros", "10 metros"],
        correctAnswer: 0,
        points: 100,
        explanation: "Distancia = velocidad × tiempo = 5 m/s × 10 s = 50 m"
      }
    ],
    "5to": [
      {
        id: "fis5_1",
        topic: "Electricidad",
        question: "¿Cuál es la resistencia de un conductor si al aplicar 12V circula una corriente de 2A?",
        options: ["6 Ω", "24 Ω", "10 Ω", "4 Ω"],
        correctAnswer: 0,
        points: 100,
        explanation: "Por la ley de Ohm: R = V/I = 12V/2A = 6Ω"
      }
    ]
  }
};

export default function Trivia() {
  const [selectedYear, setSelectedYear] = useState(null);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [answers, setAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [yearLocked, setYearLocked] = useState(false);
  const [score, setScore] = useState(0);
  const [streak, setStreak] = useState(0);
  const [timeLeft, setTimeLeft] = useState(30);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    let timer;
    if (isPlaying && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);
    } else if (timeLeft === 0) {
      setShowResults(true);
    }
    return () => clearInterval(timer);
  }, [isPlaying, timeLeft]);

  const handleYearSelect = (year) => {
    setSelectedYear(year);
    setYearLocked(true);
    setSelectedCourse(null);
    setAnswers({});
    setShowResults(false);
    setScore(0);
    setStreak(0);
  };

  const handleCourseSelect = (course) => {
    setSelectedCourse(course);
    setAnswers({});
    setShowResults(false);
    setTimeLeft(30);
    setIsPlaying(true);
  };

  const handleAnswerSelect = (questionId, answerIndex) => {
    const currentQuestion = questionBank[selectedCourse][selectedYear].find(q => q.id === questionId);
    const isCorrect = currentQuestion.correctAnswer === answerIndex;

    setAnswers(prev => ({
      ...prev,
      [questionId]: answerIndex
    }));

    if (isCorrect) {
      setScore(prev => prev + currentQuestion.points);
      setStreak(prev => prev + 1);
    } else {
      setStreak(0);
    }
  };

  const resetTrivia = () => {
    setYearLocked(false);
    setSelectedYear(null);
    setSelectedCourse(null);
    setAnswers({});
    setShowResults(false);
    setScore(0);
    setStreak(0);
    setIsPlaying(false);
    setTimeLeft(30);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white p-8">
      <div className="max-w-4xl mx-auto bg-white rounded-2xl shadow-xl p-8">
        {/* Barra de estado del juego */}
        {isPlaying && (
          <div className="flex justify-between items-center bg-blue-50 p-6 rounded-xl mb-8 shadow-md">
            <div className="flex items-center space-x-8">
              <div className="flex items-center bg-white px-4 py-2 rounded-lg shadow">
                <span className="text-2xl mr-2">⭐</span>
                <span className="text-lg font-semibold">{score} puntos</span>
              </div>
              <div className="flex items-center bg-white px-4 py-2 rounded-lg shadow">
                <span className="text-2xl mr-2">🏆</span>
                <span className="text-lg font-semibold">Racha: {streak}</span>
              </div>
            </div>
            <div className="flex items-center bg-white px-4 py-2 rounded-lg shadow">
              <span className="text-2xl mr-2">⏱️</span>
              <span className="text-lg font-semibold">{timeLeft}s</span>
            </div>
          </div>
        )}

        <h1 className="text-4xl font-bold text-center mb-8 text-blue-800">Trivia Educativa</h1>

        {/* Selección de año */}
        {!yearLocked && (
          <div className="text-center mb-12">
            <h2 className="text-2xl font-semibold mb-6 text-gray-700">Selecciona el año:</h2>
            <div className="flex justify-center gap-6">
              {['4to', '5to'].map(year => (
                <button
                  key={year}
                  onClick={() => handleYearSelect(year)}
                  className={`px-8 py-4 rounded-xl text-lg font-semibold transform transition-all duration-200 hover:scale-105 ${
                    selectedYear === year 
                      ? 'bg-blue-600 text-white shadow-lg' 
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  {year === '4to' ? '1º BGU' : '2º BGU'}
                </button>
              ))}
            </div>
          </div>
        )}

        {/* Selección de materia */}
        {selectedYear && !selectedCourse && (
          <div className="text-center mb-12">
            <h2 className="text-2xl font-semibold mb-6 text-gray-700">Selecciona la materia:</h2>
            <div className="grid grid-cols-2 gap-6">
              {Object.keys(questionBank).map(course => (
                <button
                  key={course}
                  onClick={() => handleCourseSelect(course)}
                  className="p-6 bg-white rounded-xl shadow-lg hover:shadow-xl transition-shadow transform hover:scale-105 duration-200"
                >
                  <div className="text-xl font-semibold text-gray-800 capitalize">{course}</div>
                </button>
              ))}
            </div>
          </div>
        )}

        {/* Preguntas */}
        {selectedYear && selectedCourse && !showResults && (
          <div className="space-y-8">
            {questionBank[selectedCourse][selectedYear].map((question) => (
              <div key={question.id} className="bg-white rounded-xl shadow-lg p-8 hover:shadow-xl transition-shadow">
                <div className="bg-blue-50 rounded-lg px-4 py-2 inline-block mb-4">
                  <h3 className="font-semibold text-blue-800">{question.topic}</h3>
                </div>
                <p className="text-lg mb-6 text-gray-700">{question.question}</p>

                <div className="space-y-3">
                  {question.options.map((option, idx) => (
                    <button
                      key={idx}
                      onClick={() => handleAnswerSelect(question.id, idx)}
                      className={`w-full p-4 text-left rounded-lg transition-colors ${
                        answers[question.id] === idx 
                          ? 'bg-blue-100 border-2 border-blue-500' 
                          : 'bg-gray-50 hover:bg-gray-100 border-2 border-transparent'
                      }`}
                    >
                      <span className="text-lg text-gray-700">{option}</span>
                    </button>
                  ))}
                </div>
              </div>
            ))}

            <div className="text-center mt-8">
              <button
                onClick={() => setShowResults(true)}
                className="px-8 py-4 bg-green-500 text-white text-lg font-semibold rounded-xl hover:bg-green-600 transform hover:scale-105 transition-all duration-200 shadow-lg"
              >
                Verificar Respuestas
              </button>
            </div>
          </div>
        )}

        {/* Resultados */}
        {showResults && (
          <div className="bg-white rounded-xl shadow-2xl p-8">
            <div className="text-center mb-8">
              <h2 className="text-3xl font-bold mb-4 text-blue-800">¡Juego terminado! 🎮</h2>
              <div className="flex justify-center gap-8 mb-6">
                <div className="bg-blue-50 px-6 py-3 rounded-xl">
                  <p className="text-2xl font-bold text-blue-800">Puntuación final: {score} ⭐</p>
                </div>
                <div className="bg-blue-50 px-6 py-3 rounded-xl">
                  <p className="text-2xl font-bold text-blue-800">Racha más larga: {streak} 🏆</p>
                </div>
              </div>
            </div>

            <div className="space-y-6">
              {questionBank[selectedCourse][selectedYear].map((question) => (
                <div key={question.id} className="bg-gray-50 rounded-xl p-6">
                  <p className="font-semibold text-lg mb-3">{question.question}</p>
                  <div className={`p-4 rounded-lg ${
                    answers[question.id] === question.correctAnswer 
                      ? 'bg-green-100 border-l-4 border-green-500' 
                      : 'bg-red-100 border-l-4 border-red-500'
                  }`}>
                    <p className="font-semibold">
                      {answers[question.id] === question.correctAnswer 
                        ? '¡Correcto! ✅' 
                        : 'Incorrecto ❌'}
                    </p>
                    <p className="mt-2 text-gray-700">{question.explanation}</p>
                  </div>
                </div>
              ))}
            </div>

            <div className="flex justify-center gap-6 mt-8">
              <button
                onClick={() => {
                  setAnswers({});
                  setShowResults(false);
                  setTimeLeft(30);
                  setIsPlaying(true);
                }}
                className="px-8 py-4 bg-blue-600 text-white text-lg font-semibold rounded-xl hover:bg-blue-700 transform hover:scale-105 transition-all duration-200 shadow-lg"
              >
                Intentar de nuevo 🔄
              </button>
              <button
                onClick={resetTrivia}
                className="px-8 py-4 bg-gray-600 text-white text-lg font-semibold rounded-xl hover:bg-gray-700 transform hover:scale-105 transition-all duration-200 shadow-lg"
              >
                Cambiar de año 🔙
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
